mapper.select:
SELECT

FROM sys_organization o1
WHERE id = #{organizationId}

mapper.update:
UPDATE sys_organization SET parent_id=#{target.id}, parent_ids=#{target.parentIds}
WHERE id=#{source.id}
UPDATE sys_organization SET parent_ids=concat(#{target.makeSelfAsParentIds()}, substring(parent_ids, LENGTH(#{source.makeSelfAsParentIds()})))
WHERE parent_ids LIKE #{source.makeSelfAsParentIds()} '%'
